import React, { Component } from 'react';
import { Alert } from 'reactstrap';
import './styles/mainstyle.css';

class Restaurant extends Component{
  state = {
    Name: '',
    Reviews:[],
    Rating:'',
    Type: '',
    Mood:'',
    Bar: '',
    Hours: '',
    Days: '',
    Catering:'',
    TakeOut: '',
    Address1: '',
    Address2:'',
    isLoading: false,
  }

  constructor(props) {
    super(props);
    this.componentDidMount = this.componentDidMount.bind(this);
    this.getDataFromDb = this.getDataFromDb.bind(this);
  }

  //function to contain the fetch
  componentDidMount() {
    //sets isLoading to true if we want to display some graphics during loading
    this.setState({ isLoading: true});
    //fetch pulls all restaurant data into an array
    this.getDataFromDb();
  }

  getDataFromDb = async () => {
    this.setState({ isLoading: true});
    var requestUrl = "/restaurants/" + this.props.match.params.id;
    console.log(requestUrl);
    const response = await fetch(requestUrl);
    const body = await response.json();
    this.setState({ Name: body.Name,
      Reviews:Array.from(body.Reviews),
      Rating:body.Rating,
      Type: body.Type,
      Mood:body.Mood,
      Bar: body.Bar,
      Hours: body.Hours,
      Days: body.Days,
      Catering:body.Catering,
      TakeOut: body.TakeOut,
      Address1: body.Street,
      Address2: body.City +", "+ body.State+" "+body.Zip
    });

      this.setState({ isLoading: false});
    };


    render() {
      return (
        <div className="App">
        <section className="profile">
        <div className="profile-details">
        <div className="row justify-content-center">
        <h3 className="form-title">Peachy Profile</h3>
        </div>
        <div className="row justify-content-center">
//        <img className="profile-picture" src={avatar} alt="user avatar"/>
        </div>
        <div className="row justify-content-center">
        <div className="descript">
        {this.state.Name}
        {this.state.Reviews}
        {this.state.Rating}
        {this.state.Type}
        {this.state.Mood}
        {this.state.Bar}
        {this.state.Hours}
        {this.state.Days}
        {this.state.Catering}
        {this.state.TakeOut}
        {this.state.Address1}
        {this.state.Address2}
        <h4> {this.state.name} </h4>
        <p>{this.state.email}</p>
        <div className="peach-score"><i className="em em-peach" title="Peachy Score"></i>{this.state.score}</div>
        <p>Started Peaching on {this.state.since}</p>
        </div>
        </div>
        </div>
        <div className="profile-details">
          {RestReserve}
        </div>
        <div className="profile-details">
          {RestReviews}
        </div>
        </section>
        </div>
      );

    }
  }
  export default Restaurant;
