import React, { Component } from 'react';
import { Alert } from 'reactstrap';

class Login extends Component {
  state = {
    email: '',
    password: '',
    isValid: false,
    isSuccess: false
  }


  constructor(props) {
    super(props);
    this.onDismissSuccess = this.onDismissSuccess.bind(this);
  }

  onDismissSuccess() {
    this.setState({ isSuccess: false });
  }

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/profile', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        password:this.state.password, // password
        email: this.state.email, }),
      });
      const body = await response.text();
      if (body === SUCCESSMESSAGE){
        this.clearState();
        this.setState({ isSuccess: true });
    }
  }
  render() {
    return (
      <div className="App">
      <section id= "signup">
      <h2>Login</h2>
      <div class="form-group">
      <Alert color="success" isOpen={this.state.isSuccess} toggle={this.onDismissSuccess}>
      Login successful!
      </Alert>
      <form onSubmit={this.handleSubmit}>
      <div>
      <input class="form-control" type="email" placeholder="Email" value={this.state.email} autocomplete="email"
      onChange={e => this.setState({ email: e.target.value })} required/>
      </div>
      <div>
      <input class="form-control" type="password" placeholder="Password" value={this.state.password} autocomplete="current-password"
      onChange={e => this.setState({ password: e.target.value })} required/>
      </div>
      <button type="submit" class="btn btn-success">Login</button>
      </form>
      </div>
      </section>
      </div>
    );
  }
}

export default Login;
