import React, { Component } from 'react';
import { Alert } from 'reactstrap';

class Login extends Component {
  state = {
    email: '',
    password: '',
    message: '',
    isValid: false,
    isSuccess: false
  }


  constructor(props) {
    super(props);
    this.onDismissSuccess = this.onDismissSuccess.bind(this);
  }

  onDismissSuccess() {
    this.setState({ isSuccess: false });
  }

  handleSubmit = async e => {
    e.preventDefault();
    const request = '/profile/' + this.state.email;
    const response = await fetch(request);
    if (response.status === 404){
      await this.setState({ message: "User does not exist",
                            isSuccess: true});
    }
    else{
      const body = await response.json();
      console.log(body);
    }


  }
  render() {
    return (
      <div className="App">
      <section id= "signup">
      <h2>Login</h2>
      <div class="form-group">
      <Alert color="success" isOpen={this.state.isSuccess} toggle={this.onDismissSuccess}>
      {this.state.message}
      </Alert>
      <form onSubmit={this.handleSubmit}>
      <div>
      <input class="form-control" type="email" placeholder="Email" value={this.state.email} autocomplete="email"
      onChange={e => this.setState({ email: e.target.value })} required/>
      </div>
      <div>
      <input class="form-control" type="password" placeholder="Password" value={this.state.password} autocomplete="current-password"
      onChange={e => this.setState({ password: e.target.value })} required/>
      </div>
      <button type="submit" class="btn btn-success">Login</button>
      </form>
      </div>
      </section>
      </div>
    );
  }
}

export default Login;
