import React, { Component } from 'react';
import { BrowserRouter } from 'react-router-dom';
import logo from './img/curly-peach.png'
import Router from './routes';
import './team08.css';

class App extends Component {
  state = {
    isAuthenticated: false,
    name: '',
    email: ''
  }

  constructor(props) {
    super(props);

    this.state = {
      isAuthenticated: false
    };
  }

  userHasAuthenticated = authenticated => {
    this.setState({ isAuthenticated: authenticated,
      name: sessionStorage.getItem("UserName"),
      email: sessionStorage.getItem("Email")});
    }

    componentDidMount(){
      var isAuthenticated = sessionStorage.getItem("isAuthenticated");
      if (isAuthenticated){
        this.userHasAuthenticated();
      }
    }

    handleLogoff = async e => {
      e.preventDefault();
      this.setState({isAuthenticated:false,
        name: '',
        email: ''});
        sessionStorage.setItem("isAuthenticated", false);
        sessionStorage.setItem("UserName", '');
        sessionStorage.setItem("Email", '');
      };

      render() {
        const childProps = {
          isAuthenticated: this.state.isAuthenticated,
          userHasAuthenticated: this.userHasAuthenticated
        };

        return (
          <div className="App">
          <nav class="navbar navbar-default fixed-top">
          <a class="navbar-brand" href="/main">
          <img src={logo} width="30" height="30" class="d-inline-block align-top" alt="Ms. Peaches"/>
          <span class="brand-text">Peachy Keen</span>
          </a>
          <div class="navbar-collapse collapse" id="navbar">
          <ul class="navbar-nav">
          <li class="active">
          <a class="men nav-link p-2" href="https://github.com/twbs/bootstrap" target="_blank" rel="noopener" aria-label="GitHub">
          Home
          </a>
          </li>
          <li class="nav-item">
          <a class="nav-link p-2 men" href="https://github.com/twbs/bootstrap" target="_blank" rel="noopener" aria-label="GitHub">
          About us
          </a>
          </li>
          <li class="nav-item">
          <a class="nav-link p-2 men" href="https://github.com/twbs/bootstrap" target="_blank" rel="noopener" aria-label="GitHub">
          </a>
          </li>
          </ul>
          </div>
          </nav>
          <BrowserRouter>
          <Router childProps={childProps}/>
          </BrowserRouter>
          </div>
        );
      }
    }



    export default App;
