import React, { Component } from 'react';
import { Alert } from 'reactstrap';
import avatar from './img/user-profile.jpg'
import './styles/mainstyle.css';

class Profile extends Component {
  state = {
    isAuthenticated: false,
    name: '',
    email: '',
    message: '',
    isSuccesss: false,
    color: 'danger'
  }

  onDismissSuccess() {
    this.setState({ isSuccess: false });
  }

  constructor(props) {
    super(props);
    this.onDismissSuccess = this.onDismissSuccess.bind(this);
    this.state = {
      isAuthenticated: false
    };
  }

  userHasAuthenticated = authenticated => {
    this.setState({ isAuthenticated: authenticated,
      name: sessionStorage.getItem("UserName"),
      email: sessionStorage.getItem("Email")});
    }

    componentDidMount(){
      var isAuthenticated = sessionStorage.getItem("isAuthenticated");
      this.userHasAuthenticated(isAuthenticated);
  }

  handleSubmit = async e => {
    this.setState({ isSuccess: false });
    e.preventDefault();
    const request = '/profiles/' + this.state.email;
    const response = await fetch(request);
    if (response.status === 404){
      await this.setState({ message: "Unexpected error occurred",
                            color: 'danger'});
    }
    else{
      const body = await response.json();
      if (body.password === this.state.password){
        sessionStorage.setItem("UserName", body.name);
        sessionStorage.setItem("isAuthenticated", true);
        sessionStorage.setItem("Email", body.email);
        this.setState({ message: "Login successful! Redirecting now!",
                        isSuccess: true,
                        color: 'success'});
        this.props.userHasAuthenticated(true);
      }
  }
}

  render() {
    return (
      <div className="App">
      <section className="profile">
      <div className="profile-details">
      <Alert color={this.state.color} isOpen="false" toggle={this.onDismissSuccess}>
      {this.state.message}
      </Alert>
      <div className="row justify-content-center">
      <h3 className="form-title">Peachy Profile</h3>
      </div>
      <div className="row justify-content-center">
      <img className="profile-picture" src={avatar} alt="user avatar"/>
      </div>
      <div className="row justify-content-center profile-content">
      Name: {this.state.name}
      </div>
      <div className="row justify-content-center profile-content">
      Email: {this.state.email}
      </div>
      <div className="row justify-content-center profile-content">
      Peachy Score:
      </div>
      <div className="row justify-content-center profile-content">
      Member Since:
      </div>
      <br/>
      </div>
      <div className="profile-reviews">
      <div className="row">
      Restaurant Name:
      </div>
      <div className="row">
      Review Date:
      </div>
      <div className="row">
      Review:
      </div>
      </div>
      </section>
      </div>
    );
  }
}

export default Profile;
