import React, { Component } from 'react';
import { Alert } from 'reactstrap';

const SUCCESSMESSAGE = "Success";

class Profile extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    reppassword:'',
    isValid: false,
    isSuccess: false
  };

  constructor(props) {
    super(props);
    this.clearState = this.clearState.bind(this);
    this.onDismissSuccess = this.onDismissSuccess.bind(this);
    this.onDismissValid = this.onDismissValid.bind(this);
  }

  onDismissSuccess() {
    this.setState({ isSuccess: false });
  }

  onDismissValid() {
    this.setState({ isValid: false });
  }

  clearState(){
    this.setState({
    name: '',
    email: '',
    password: '',
    reppassword:'',
    isValid: false });
  }

  handleSubmit = async e => {
    const { password, reppassword } = this.state;
    if ( password !== reppassword ){
      this.setState({ isValid: true });
    }
    else{
      e.preventDefault();
      const response = await fetch('/profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: this.state.name, // name
          password:this.state.password, // password
          email: this.state.email, }),
        });
        const body = await response.text();
        if (body === SUCCESSMESSAGE){
          this.clearState();
          this.setState({ isSuccess: true });
        }

      }
    };
    render() {
      return (
        <div className="App">
        <section id= "signup">
        <h2>Signup</h2>
        <div class="form-group">
        <Alert color="success" isOpen={this.state.isSuccess} toggle={this.onDismissSuccess}>
        User created successfully!
        </Alert>
        <form onSubmit={this.handleSubmit}>
        <div>
        <input class="form-control" type="text" placeholder="Name" value={this.state.name}
        onChange={e => this.setState({ name: e.target.value })} required/>
        </div>
        <div>
        <input class="form-control" type="email" placeholder="Email" value={this.state.email}
        onChange={e => this.setState({ email: e.target.value })} required/>
        </div>
        <div>
        <input class="form-control" type="password" placeholder="Password" value={this.state.password}
        onChange={e => this.setState({ password: e.target.value })} required/>
        </div>
        <div>
        <input class="form-control" type="password" placeholder="Repeat password" value={this.state.reppassword}
        onChange={e => this.setState({ reppassword: e.target.value })} required/>
        <Alert color="info" isOpen={this.state.isValid} toggle={this.onDismissValid}>
        Passwords do not match!
        </Alert>
        </div>
        <div>
        <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>
        </div>
        <div class="button_pair">
        <button type="button" class="btn btn-danger" onClick={this.clearState} >Cancel</button>
        <button type="submit" class="btn btn-success">Sign Up</button>
        </div>
        <button type="button" class="btn btn-info">Already Have A Login?</button>
        </form>
        </div>
        </section>
        </div>
      );
    }
  }

  export default Profile;
