import React, { Component } from 'react';
import { Alert } from 'reactstrap';

const SUCCESSMESSAGE = "Success";

class Profile extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    reppassword:'',
    responseToPost: '',
    isValid: true,
    isSuccess: false
  };

  constructor(props) {
    super(props);
    this.onDismiss = this.onDismiss.bind(this);
  }

  onDismiss() {
    this.setState({ visible: false });
  }

  clearState(){
    this.setState({name: '',
    email: '',
    password: '',
    reppassword:'',
    responseToPost: '',
    isValid: true });
  }

  handleSubmit = async e => {
    const { password, reppassword } = this.state;
    if ( password !== reppassword ){
      alert("Passwords don't match");
    }
    else{
      e.preventDefault();
      const response = await fetch('/profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: this.state.name, // name
          password:this.state.password, // password
          email: this.state.email, }),
        });
        const body = await response.text();
        this.setState({ responseToPost: body });
        if (body == SUCCESSMESSAGE){
          this.clearState();
        }

      }
    };
    render() {
      return (
        <div className="App">
        <section id= "signup">
        <h2>Signup</h2>
        <div class="form-group">
        <Alert color="info" isOpen={this.state.visible} toggle={this.onDismiss}>
        I am an alert and I can be dismissed!
        </Alert>
        <form onSubmit={this.handleSubmit}>
        <div>
        <input class="form-control" type="text" placeholder="Name" value={this.state.name}
        onChange={e => this.setState({ name: e.target.value })} required/>
        </div>
        <div>
        <input class="form-control" type="email" placeholder="Email" value={this.state.email}
        onChange={e => this.setState({ email: e.target.value })} required/>
        </div>
        <div>
        <input class="form-control" type="password" placeholder="Password" value={this.state.password}
        onChange={e => this.setState({ password: e.target.value })} required/>
        </div>
        <div>
        <input class="form-control" type="password" placeholder="Repeat password" value={this.state.reppassword}
        onChange={e => this.setState({ reppassword: e.target.value })} required/>
        <Alert color="info" isOpen={this.state.visible} toggle={this.onDismiss}>
        I am an alert and I can be dismissed!
        </Alert>
        </div>
        <div>
        <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>
        </div>
        <div>
        <button type="button" class="btn btn-danger" >Cancel</button>
        <button type="submit" class="btn btn-success">Sign Up</button>
        </div>
        <button type="button" class="btn btn-info">Already Have A Login?</button>
        </form>
        </div>
        </section>
        </div>
      );
    }
  }

  export default Profile;
