import React, { Component } from 'react';
import { BrowserRouter } from 'react-router-dom';
import logo from './img/curly-peach.png'
import Router from './routes';
import './team08.css';

class App extends Component {
  state = {
    isAuthenticated: false,
    name: '',
    email: ''
  }

  constructor(props) {
    super(props);

    this.state = {
      isAuthenticated: false
    };
  }

  userHasAuthenticated = authenticated => {
    this.setState({ isAuthenticated: authenticated,
      name: sessionStorage.getItem("UserName"),
      email: sessionStorage.getItem("Email")});
    }

    componentDidMount(){
      var isAuthenticated = sessionStorage.getItem("isAuthenticated");
      if (isAuthenticated){
        this.userHasAuthenticated();
      }
    }

    handleLogoff = async e => {
      e.preventDefault();
      this.setState({isAuthenticated:false,
        name: '',
        email: ''});
        sessionStorage.setItem("isAuthenticated", false);
        sessionStorage.setItem("UserName", '');
        sessionStorage.setItem("Email", '');
      };

      render() {
        const childProps = {
          isAuthenticated: this.state.isAuthenticated,
          userHasAuthenticated: this.userHasAuthenticated
        };

        return (
          <div className="App">
          <nav class="navbar navbar-default navbar-fixed-top">
      <div class="container">
        <div class="navbar-header">
          <button type="button" class="navbar-toggle collapsed" data-toggle="collapse" data-target="#navbar" aria-expanded="false" aria-controls="navbar">
            <span class="sr-only">Toggle navigation</span>
            <span class="icon-bar"></span>
            <span class="icon-bar"></span>
            <span class="icon-bar"></span>
          </button>
          <a class="navbar-brand" href="#">Project name</a>
        </div>
        <div id="navbar" class="navbar-collapse collapse">
          <ul class="nav navbar-nav">
            <li class="active"><a href="#">Home</a></li>
            <li><a href="#">About</a></li>
            <li><a href="#">Contact</a></li>
            <li class="dropdown">
              <a href="#" class="dropdown-toggle" data-toggle="dropdown" role="button" aria-haspopup="true" aria-expanded="false">Dropdown <span class="caret"></span></a>
              <ul class="dropdown-menu">
                <li><a href="#">Action</a></li>
                <li><a href="#">Another action</a></li>
                <li><a href="#">Something else here</a></li>
                <li role="separator" class="divider"></li>
                <li class="dropdown-header">Nav header</li>
                <li><a href="#">Separated link</a></li>
                <li><a href="#">One more separated link</a></li>
              </ul>
            </li>
          </ul>
          <ul class="nav navbar-nav navbar-right">
            <li><a href="../navbar/">Default</a></li>
            <li><a href="../navbar-static-top/">Static top</a></li>
            <li class="active"><a href="./">Fixed top <span class="sr-only">(current)</span></a></li>
          </ul>
        </div>
      </div>
    </nav>
          <nav class="navbar navbar-light bg-light">
          <a class="navbar-brand" href="#">
          <img src={logo} width="60" height="60" class="d-inline-block align-top" alt="Ms. Peaches"/>
          Peachy Keen</a>
          <ul class="navbar-nav flex-row ml-md-auto d-none d-md-flex">
          <li class="nav-item">
          <a class="men nav-link p-2" href="https://github.com/twbs/bootstrap" target="_blank" rel="noopener" aria-label="GitHub">
          ABOUT PPD
          </a>
          </li>
          <li class="nav-item">
          <a class="nav-link p-2 men" href="https://github.com/twbs/bootstrap" target="_blank" rel="noopener" aria-label="GitHub">
          OUR STORY
          </a>
          </li>
          <li class="nav-item">
          <a class="nav-link p-2 men" href="https://github.com/twbs/bootstrap" target="_blank" rel="noopener" aria-label="GitHub">
          GET INVOLVED
          </a>
          </li>
          <div class = "nav_container">
          <div class = "nav_container_line"></div>
          <div class = "nav_container_line"></div>
          <div class = "nav_container_line"></div>
          <nav class="nav_small">
          <a href="/">Home</a>
          <a href="/Profile">Sign up</a>
          <a href="/About">About us</a>
          <a href="/Login">Login</a>
          </nav>
          </div>
          </ul>
          </nav>

          Welcome {this.state.name}! {this.state.email} <a href="Main" onClick={this.handleLogoff}> Logoff! </a>
          <BrowserRouter>
          <Router childProps={childProps}/>
          </BrowserRouter>
          </div>
        );
      }
    }



    export default App;
