import React, { Component } from 'react';
import { BrowserRouter } from 'react-router-dom';
import logo from './img/curly-peach.png'
import Router from './routes';
import './team08.css';

class App extends Component {
  state = {
    isAuthenticated: false,
    name: '',
    email: ''
  }

  constructor(props) {
    super(props);

    this.state = {
      isAuthenticated: false
    };
  }

  userHasAuthenticated = authenticated => {
    this.setState({ isAuthenticated: authenticated,
      name: sessionStorage.getItem("UserName"),
      email: sessionStorage.getItem("Email")});
    }

    componentDidMount(){
      var isAuthenticated = sessionStorage.getItem("isAuthenticated");
      if (isAuthenticated){
        this.userHasAuthenticated();
      }
    }

    handleLogoff = async e => {
      e.preventDefault();
      this.setState({isAuthenticated:false,
        name: '',
        email: ''});
        sessionStorage.setItem("isAuthenticated", false);
        sessionStorage.setItem("UserName", '');
        sessionStorage.setItem("Email", '');
      };

      render() {
        const childProps = {
          isAuthenticated: this.state.isAuthenticated,
          userHasAuthenticated: this.userHasAuthenticated
        };

        return (
          <div className="App">
          <nav class="navbar navbar-expand-lg navbar-light bg-light fixed-top">
          <a class="navbar-brand" href="/main">
          <img src={logo} width="30" height="30" class="d-inline-block align-top" alt="Ms. Peaches"/>
          <span class="brand-text">Peachy Keen</span>
          </a>
          <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
          </button>

          <div class="collapse navbar-collapse" id="navbarSupportedContent">
          <ul class="navbar-nav mr-auto">
          <li class="nav-item active">
          <a class="nav-link p-2 men" href="/Main" aria-label="Home">
          Home
          </a>
          </li>
          <li class="nav-item">
          <a class="nav-link p-2 men" href="/About" aria-label="About us">
          About us
          </a>
          </li>
          </ul>
          <div class="user-box">
          {this.state.isAuthenticated
            ? <a class="nav-link p-2 men" href="#" onClick={this.handleLogoff}><i class="fas fa-user-plus"></i>{'\u00A0'}Logout</a>
            : <a class="nav-link p-2 men" href="/Profile"><i class="fas fa-user-plus"></i>{'\u00A0'}Sign up</a>
          }
          </div>
          </div>
          </nav>
          <BrowserRouter>
          <Router childProps={childProps}/>
          </BrowserRouter>
          </div>
        );
      }
    }



    export default App;
