import React, { Component } from 'react';


class Profile extends Component {
  state = {
    name: '',
    email: '',
    password: '',
    reppassword:'',
    responseToPost: '',
    isValid: true
  };
  componentDidMount() {
    this.callApi()
    .then(res => this.setState({ response: res.express }))
    .catch(err => console.log(err));
  }

  handleSubmit = async e => {
    if (this.state.isValid){
      e.preventDefault();
      const response = await fetch('/profile', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: req.body.name, // name
          password:req.body.pass, // password
          email: req.body.email, }),
        });
        const body = await response.text();
        this.setState({ responseToPost: body });
      }
    };
    render() {
      return (
        <div className="App">
        <section id= "signup">
        <h2>Signup</h2>
        <div class="form-group">
        <form onSubmit={this.handleSubmit}>
        <div>
        <input class="form-control" type="text" placeholder="Name" value={this.state.name} required/>
        </div>
        <div>
        <input class="form-control" type="email" placeholder="Email" value={this.state.email} required/>
        </div>
        <div>
        <input class="form-control" type="password" placeholder="Password" value={this.state.password} required/>
        </div>
        <div>
        <input class="form-control" type="password" placeholder="Repeat password" value={this.state.reppassword} required/>
        </div>
        <div>
        <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>
        </div>
        <div>
        <button type="button" class="btn btn-danger" >Cancel</button>
        <button type="submit" class="btn btn-success">Sign Up</button>
        </div>
        <button type="button" class="btn btn-info">Already Have A Login?</button>
        </form>
        </div>
        </section>
        </div>
      );
    }
  }

  export default Profile;
